func findPrimes(n int) []int {
    var primes []int

    // Base case: if n is 1, return [1]
    if n == 1 {
        primes = []int{1}
        return primes
    }

    // Sieve of Eratosthenes: loop through all integers from 2 to sqrt(n)
    for i := 2; i <= math.Sqrt(n); i++ {
        if n%i == 0 {
            // If i divides n, it must also divide all primes less than n
            for j := 2; j <= math.Sqrt(n); j++ {
                if j%i == 0 {
                    primes = append(primes, j)
                }
            }
        }
    }

    return primes
}

func printPrimes(primes []int) {
    // Sort the list of primes
    sort.Ints(primes)

    // Print the primes
    for i := 0; i < len(primes); i++ {
        fmt.Println(primes[i])
    }
}
```
