```go
package main

import (
	"fmt"
	"sync"
)

type Producer struct {
	mutex sync.Mutex
	items []string
}

func (p *Producer) Produce(item string) {
	p.mutex.Lock()
	defer p.mutex.Unlock()
	p.items = append(p.items, item)
}

type Consumer struct {
	mutex sync.Mutex
	items []string
}

func (c *Consumer) Consume() {
	c.mutex.Lock()
	defer c.mutex.Unlock()
	for _, item := range c.items {
		fmt.Println(item)
	}
	c.items = []string{}
}

func main() {
	producer := &Producer{
		items: []string{"apple", "banana", "cherry"},
	}
	consumer := &Consumer{
		items: []string{"orange", "grape", "pear"},
	}

	go producer.Produce("pear")
	go consumer.Consume()

	time.Sleep(time.Second)
}
```

