        continue;
      }

      // Do stuffs
      console.log(`message ${message.id} processed`);

      await this.mq.deleteMessageAsync({
        qname: config.WORKER_QUEUE_NAME,
        id: message.id,
      });

      console.log('deleted message with id', message.id);
    }
  }
}

