def is_prime(limit):
    primes = [True] * limit
    primes[0] = primes[1] = False
    for i in range(2, limit):
        if primes[i]:
            for j in range(i * i, limit, i):
                primes[j] = False
    return primes

def is_leap_year(year):
    return (year % 4 == 0 and year % 100!= 0) or (year % 400 == 0)

