def square_root(number):
    if number < 0:
        raise ValueError("Square root of a negative number is not defined.")

    if number == 0:
        return 0

    small_number = 1
    big_number = number

    while big_number > 0:
        mid_number = (big_number + small_number) // 2
        if mid_number ** 2 == big_number:
            return mid_number
        elif mid_number ** 2 > big_number:
            big_number = mid_number ** 2 - big_number
            small_number = mid_number
        else:
            big_number = mid_number ** 2
            small_number = mid_number - 1

def main():
    number = int(input("Enter a number: "))
    result = square_root(number)
    print(f"The square root of {number} is {result}")

if __name__ == "__main__":
