package main

import (
	"fmt"
	"sync"
)

type Producer struct {
	channel chan int
	mutex   sync.Mutex
}

func NewProducer() *Producer {
	p := &Producer{}
	p.channel = make(chan int)
	return p
}

func (p *Producer) Send(num int) {
	p.mutex.Lock()
	p.channel <- num
	p.mutex.Unlock()
}

func (p *Producer) Receive() int {
	p.mutex.Lock()
	num := <-p.channel
	p.mutex.Unlock()
	return num
}

func main() {
	producer := NewProducer()
	consumer := NewConsumer(producer.channel)

	// Producer generates numbers
	for i := 0; i < 10; i++ {
		producer.Send(i)
	}

	// Consumer consumes numbers
	for {
		fmt.Printf("Consumed: %v\n", consumer.Receive())
	}
}
```
