```javascript
function findObjectByName(obj, name) {
  const stack = [];
  const traverse = (obj, path) => {
    if (obj === null || obj === undefined) {
      return;
    }
    if (typeof obj === 'object' && obj!== null) {
      for (const key in obj) {
        if (obj.hasOwnProperty(key)) {
          if (key === name) {
            return path;
          }
          stack.push(key);
          traverse(obj[key], path + '.' + key);
          stack.pop();
        }
      }
    }
  };
  traverse(obj, '');
  return stack.pop();
}
```
